package pack4;





public class Ex31StudDto {
	
	
	private int guk,en;
	private String irum;
	public Ex31StudDto() {
		// TODO Auto-generated constructor stub
	}
	public Ex31StudDto(int guk,int en,String irum) {
		this.guk=guk;
		this.en=en;
		this.irum=irum;
	
	}
	public int getGuk() {
		return guk;
	}
	public void setGuk(int guk) {
		this.guk = guk;
	}
	public int getEn() {
		return en;
	}
	public void setEn(int en) {
		this.en = en;
	}
	public String getIrum() {
		return irum;
	}
	public void setIrum(String irum) {
		this.irum = irum;
	}
	
	
	
}
